-- DESAFIO 1.1SELECT c.CompanyName, oh.SalesOrderID, oh.TotalDueFROM SalesLT.Customer AS cJOIN SalesLT.SalesOrderHeader AS oh    ON oh.CustomerID = c.CustomerID;-- DESAFIO 1.2SELECT c.CompanyName,       a.AddressLine1,       ISNULL(a.AddressLine2, '') AS AddressLine2,       a.City,       a.StateProvince,       a.PostalCode,       a.CountryRegion,       oh.SalesOrderID,       oh.TotalDueFROM SalesLT.Customer AS cJOIN SalesLT.SalesOrderHeader AS oh    ON oh.CustomerID = c.CustomerIDJOIN SalesLT.CustomerAddress AS ca    ON c.CustomerID = ca.CustomerIDJOIN SalesLT.Address AS a    ON ca.AddressID = a.AddressIDWHERE ca.AddressType = 'Main Office';-- DESAFIO 2.1SELECT c.CompanyName, c.FirstName, c.LastName,       oh.SalesOrderID, oh.TotalDueFROM SalesLT.Customer AS cLEFT JOIN SalesLT.SalesOrderHeader AS oh    ON c.CustomerID = oh.CustomerIDORDER BY oh.SalesOrderID DESC;-- DESAFIO 2.2SELECT c.CompanyName, c.FirstName, c.LastName, c.PhoneFROM SalesLT.Customer AS cLEFT JOIN SalesLT.CustomerAddress AS ca    ON c.CustomerID = ca.CustomerIDWHERE ca.AddressID IS NULL;--DESAFIO 3.1SELECT pcat.Name AS ParentCategory, cat.Name AS SubCategory, prd.Name AS ProductNameFROM SalesLT.ProductCategory pcatJOIN SalesLT.ProductCategory as cat    ON pcat.ProductCategoryID = cat.ParentProductCategoryIDJOIN SalesLT.Product as prd    ON prd.ProductCategoryID = cat.ProductCategoryIDORDER BY ParentCategory, SubCategory, ProductName;